import Vue from "vue";
import Vuex from "vuex";
import axios from "axios";

import register from "./register";

import currencyService from "../services/CurrencyService";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    cryptos: [],
  },
  getters: {
    cryptos(state) {
      return state.cryptos;
    },
  },
  mutations: {
    ADD_CRYPTO(state, payload) {
      state.cryptos = payload;
    },
  },
  actions: {
    async getCryptoCurrencies({ commit }) {
      const { data } = await axios.get("http://localhost:5000/api-v1/currency");
      if (data.status === 200) {
        commit(
          "ADD_CRYPTO",
          data.result.sort((a, b) => {
            return a.id - b.id;
          }),
        );
        localStorage.setItem("cryptosAll", JSON.stringify(data.result));
        localStorage.setItem("selectCurrency", JSON.stringify("Bitcoin BTC"));
        return data.result;
      }
    },
  },
  modules: {
    register,
  },
});
