import currencyService from "../services/CurrencyService";

export default {
  state: {
    cryptosExchange: [],
  },
  getters: {
    cryptosExchange(state) {
      return state.cryptos;
    },
  },
  mutations: {
    ADD_CRYPTO(state, payload) {
      state.cryptos = payload;
    },
  },
  actions: {
    async getCryptoCurrenciesExchange({ commit }) {
      const { data } = await currencyService.getCurrency();
      if (data.status === 200) {
        commit(
          "ADD_CRYPTO",
          data.result
            .sort((a, b) => {
              return a.id - b.id;
            })
            .filter((e) => {
              return (
                e.name_currency !== localStorage.getItem("giveCurrency").replace(/['"«»]/g, "")
              );
            }),
        );
        return data.result;
      }
    },
  },
};
