import { email, minLength, required, sameAs } from "vuelidate/lib/validators";
export default {
  name: "register-view",
  data: function data() {
    return {
      formRegister: {
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
        ruleRead: false
      },
      minLengthText: "Минимальная длина 8 символов!",
      passwordConfirmText: "Пароли не совпадают"
    };
  },
  methods: {
    status: function status(validation) {
      return {
        "is-invalid": validation.$error
      };
    },
    registerSubmitForm: function registerSubmitForm() {
      this.$v.$touch();

      if (this.$v.$invalid) {
        console.log("Error");
      } else {
        console.log("Success");

        try {
          var formData = {
            username: this.formRegister.username,
            email: this.formRegister.email,
            password: this.formRegister.password
          };
          this.$store.dispatch("registration", formData);
        } catch (error) {
          console.log("Error in registerSubmitForm in RegisterView func");
        }
      }
    }
  },
  validations: {
    formRegister: {
      username: {
        required: required
      },
      email: {
        email: email,
        required: required
      },
      password: {
        required: required,
        minLength: minLength(8)
      },
      confirmPassword: {
        required: required,
        sameAs: sameAs("password")
      },
      ruleRead: {
        checked: function checked(val) {
          return val === true ? true : false;
        },
        required: required
      }
    }
  }
};
